import React, { useEffect, useState } from 'react'
import { createScanner } from 'typescript'
import { productController } from '../../controller/ProductController'
import ItemCart, { getListFromLocal } from '../../model/Cart'
import { CartItem } from '../../model/CartItem'
import { Product } from '../../model/Product'
import './CartPage.css'
import ItemCartRender from './ItemCartRender'


export interface State{
    carts:ItemCart[],
    CartItems:CartItem[]
}

export default function CartPage() {

    const [state,setState]=useState<State>({
        carts:getListFromLocal(),
        CartItems:[]
    })

    console.log(state.CartItems);


    useEffect(() => {
        let listCarts:CartItem[]
        // productController.list().then(res=>res.map(item=>{
        //     let carts=state.carts;
        //     let itemCart:CartItem ={id:"",name:"",price:0,image:"",quantity:0};
        //     for(let i=0;i<state.carts.length;i++){
        //         if(item.id===carts[i].id){
        //             itemCart.id=item.id,
        //             itemCart.image=item.image,
        //             itemCart.name=item.name,
        //             itemCart.price=item.price,
        //             itemCart.quantity=Number(carts[i].quantity),
        //             listCarts.push(itemCart)
        //         }
        //     }
        //     console.log(listCarts);
        // }))
        let listProduct:Product[]=[];
        productController.list().then(res=>console.log(res))
        console.log(listProduct);




    },[])


    const showCart=()=>{
        if(state.carts.length>0){
            return  <div>
                        <table className="show-list">
                            <tr className="table-title">
                                <th><p>Product</p></th>
                                <th><p>Quantity</p></th>
                                <th><p>Subtotal</p></th>
                            </tr>

                            {state.CartItems.map(item=><ItemCartRender key={+item.id} itemCart={item}/>)}

                            <tr className="table-row">
                                <td></td>
                                <td className='table-column-2'>Total</td>
                                <td className='table-column-3'>600$</td>
                            </tr>
                        </table>
                        <button className='btn'>Order</button>
                    </div>      
        }else{
            return <div className='cart-empty'>
                <h1>Your cart is Empty</h1>
            </div>
        }
    }

    return (
        <div id="cart-page-container">
            <div className="cart-page">
                <h1 className="cart-page-title">Your Cart:</h1>
                <div className="show-list-container">
                    {showCart()}
                </div>
            </div>
        </div>
    )
}
