import { Buyer } from "./Buyer";
import { Product } from "./Product";

export default interface ItemCart{
    id:String
    quantity:Number
}

export const getListFromLocal =()=>{
    let cartsFromLocal = localStorage.getItem('carts')
    let carts:ItemCart[]=[];
    if (cartsFromLocal) {
        const list = JSON.parse(cartsFromLocal)
        if(list.length===0){
            localStorage.setItem('carts',JSON.stringify([]))
            carts=[]
        }else{
            carts=list
        }
    }else{
        localStorage.setItem('carts',JSON.stringify([]))
        carts=[]
    }
    return carts
}

export const setListToCart = (list:ItemCart[])=>{
    localStorage.setItem('carts',JSON.stringify(list))
}
