import './Products.css'
import React, { useEffect } from 'react'
import {ProductItem} from './ProductItem'
import { listIphone, Product } from '../../model/Product'
import { useState } from 'react'
import Form from './Form'
import { v4 as uuid } from 'uuid';
import {listProduct} from '../../model/Product'

interface State {
    products: Product[]
}

export default function Products() {

    const [state, setState] = useState<State>({products:listIphone});


    const listProduct =()=>{
        if (localStorage.getItem("listProduct") === null) {
            localStorage.setItem('listProduct',JSON.stringify(listIphone))
            console.log(listIphone);
        }else{
            let listIphoneLocal = localStorage.getItem('listProduct')
            if (listIphoneLocal && listIphoneLocal) {
                let listPro:Product[];
                let pro:Product
                let list = JSON.parse(listIphoneLocal)

