import React, { useEffect, useState } from 'react'
import { createScanner } from 'typescript'
import { productController } from '../../controller/ProductController'
import { getListFromLocal ,ItemCart} from '../../model/ItemCart'
import { Product } from '../../model/Product'
import './CartPage.css'
import ItemCartRender from './ItemCartRender'


export interface State{
    itemCarts:ItemCart[],
    totalMoney:number
}

export default function CartPage() {

    const [state,setState]=useState<State>({
        itemCarts:getListFromLocal(),
        totalMoney:0
    })

    useEffect(()=>{
        let totalMoney=0
        state.itemCarts.forEach(element => {
            totalMoney+= (element.price*element.quantity)
        });
        setState({...state,totalMoney:totalMoney})
    },[])

    const onChangeQuantity=(quantity:number,id:string)=>{
        let index = state.itemCarts.findIndex(item=>item.id===id)
        let itemCartsFake=state.itemCarts
        itemCartsFake[index].quantity=quantity
        let totalMoney=0
        itemCartsFake.forEach(element => {
            totalMoney+= (element.price*element.quantity)
        });

        setState({...state,itemCarts:itemCartsFake,totalMoney:totalMoney})

        // setState({...state,itemCarts :state.itemCarts[Number(index)].quantity=quantity})



        // let index = state.itemCarts.find(item=>item.id===props.itemCart.id)
        // setState({...state,itemCarts : state.itemCarts[Number(index)].quantity=e})
    }


    const showCart=()=>{
        if(state.itemCarts.length>0){
            return  <div>
                        <table className="show-list">
                            <tr className="table-title">
                                <th><p>Product</p></th>
                                <th><p>Quantity</p></th>
                                <th><p>Subtotal</p></th>
                            </tr>

                            {state.itemCarts.map(item=><ItemCartRender key={+item.id} itemCart={item} onChangeQuantity={onChangeQuantity} />)}

                            <tr className="table-row">
                                <td></td>
                                <td className='table-column-2'>Total</td>
                                <td className='table-column-3'>{state.totalMoney} $</td>
                            </tr>
                        </table>
                        <button className='btn'>Order</button>
                    </div>      
        }else{
            return <div className='cart-empty'>
                <h1>Your cart is Empty</h1>
            </div>
        }
    }

    return (
        <div id="cart-page-container">
            <div className="cart-page">
                <h1 className="cart-page-title">Your Cart:</h1>
                <div className="show-list-container">
                    {showCart()}
                </div>
            </div>
        </div>
    )
}
