import './Products.css'
import React from 'react'
import {ProductItem} from './ProductItem'
import { listIphone, Product } from '../../model/Product'
import { useState } from 'react'


interface State {
    products: Product[]
}

export default function Products() {
    const [state, setState] = useState<State>({products:listIphone});

    const [newProduct, setNewProduct] = useState<Product>({productId:"12",name:"",price:0,image:""});

    const [formData,setFormData] = useState({idProduct:"",name:"",price:Number,image:""});


    const onDelete = (idProduct:string) => {
        let listProduct=state.products;
        listProduct=listProduct.filter(item=>item.productId!=idProduct)
        setState({products:listProduct})
    };

    const inputHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({...formData, [e.target.name]: e.target.value})
        console.log(formData);
    };

    const addProduct=(product:Product) =>{
        let listProduct=state.products;
        listProduct.push(product)
        setState({products:listProduct})
    }


    return (
        <section id="product-container">
            <div className="title-button">
                <h1 className="product-container-title">Danh Sách sản phẩm</h1>

            </div>
            <div className="form-add">
                <div className="form-container">
                    <div className="form-input">
                        <input type="text" name="name" onChange={inputHandler} placeholder='Name' className="name-product" />
                        <input type="text" name="price" onChange={inputHandler} placeholder="Price" className="price-product" />
                        <input type="text" name="image" onChange={inputHandler} placeholder="ImageUrl" className="img-product" />
                        <input type="text" name="desc" onChange={inputHandler} placeholder="Desc" className="desc-product" />
                    </div>
                    <div className="group-button">
                        <button className="edit-product btn">Add</button>
                        <button className="edit-product btn">Edit</button>
                        <button className="edit-product btn">Remove</button>
                    </div>
                </div>
            </div>
            <div className="list-product">
                {state.products.map((item)=>  <ProductItem product={item} onDelete={onDelete} />)}
            </div>

        </section>
    )
}
