import React from 'react'
import { Link, useParams } from 'react-router-dom'


interface Props{
    page:number
    setPage:(number:number)=>void
}

export default function Pagination(props:Props) {
    const param = +(useParams())
    console.log(param===props.page);

    return (
        <Link to={`/shop/${props.page}`}>
            <span onClick={()=>props.setPage(props.page)} 
            style={param===props.page? {border:"red"}:{backgroundColor:"1px solid #393D72"}}>{props.page}</span>
        </Link>
    )
}
