import './App.css';
import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import HomePage from './page/home/HomePage';
import Header from './component/Header';
import Footer from './component/Footer';
import Products from './page/admin/ProductsAdmin';
import CartPage from './page/cart/CartPage';
import { Product } from './model/Product'
import { v4 as uuid } from 'uuid';
import {getListProduct} from './model/Product'
import ShopPage from './page/shop/ShopPage';
import axios from 'axios'
import { TestApi } from './api/Axios';


export interface State {
  products: Product[]
  product: Product
  isEdit:Boolean
  showPopup:Boolean
}

function App() {

  const [state, setState] = useState<State>({
    products: getListProduct(),
    product:  {productId:"",name:"",price:0,image:""},
    isEdit:false,
    showPopup:false
  });


  // const [productEdit,setProductEdit] = useState<Product>({productId:"",name:"",price:0,image:""})
  // const [isEdit,setIsEdit]=useState<Boolean>(false)
  const [showPopup,setShowPopup]=useState<Boolean>(false)

  useEffect(() => {
    axios.get<Product[]>('http://localhost:3333/api')
    .then((response) => {
      setState({...state,products:response.data})
    });
  }, []);

  const onEditForm =(isEdit:Boolean)=>{
    setState({...state,isEdit:isEdit})
  }

  const onDelete = (idProduct:string) => {
    let listProduct=state.products;
    listProduct=listProduct.filter(item=>item.productId !== idProduct)
    setState({...state,products:listProduct})
    // setListProductToLocal(listProduct);
  };

  const onAddEditProduct=(product:Product) =>{
    let listProduct:Product[]=state.products;
    if(product.productId===""){
        product.productId=uuid();
        listProduct.push(product)
        let productClear={productId:"",name:"",price:0,image:"",desc:""};
        setState({...state,product:productClear})
    }else{
        let index =listProduct.findIndex(item => item.productId === product.productId)
        listProduct[index] = product;
        let productClear={productId:"",name:"",price:0,image:"",desc:""};
        setState({...state,product:productClear})
    }
    setState({...state,products:listProduct})
    // setListProductToLocal(listProduct);
  }

  const onClickShowInfo =(productShow:Product)=>{
    setState({...state,product:productShow,isEdit:true,showPopup:true})
    // setIsEdit(true)
    // setShowPopup(true)
  }

  const setListProductToLocal=(listProduct:Product[])=>{
    localStorage.setItem('listProduct',JSON.stringify(listProduct))
  }

  //cart

  return (
    <Router>
      <div>
          <Header/>
            <Routes>
              <Route path="/" element={<HomePage/>}/>
              <Route path="home" element={<HomePage/>}/>
              <Route path="shop" element={<ShopPage key={uuid()}   listProduct={state.products}/>}/>
              <Route path="admin" element={<Products  key={uuid()}
                                                    onAddEditProduct={onAddEditProduct}
                                                    productEdit={state.product} 
                                                    onDelete={onDelete} 
                                                    onClickShowInfo={onClickShowInfo}
                                                    products={state.products}
                                                    isEdit={state.isEdit}
                                                    onEditForm={onEditForm}
                                                    showPopup={state.showPopup}
                                                    setShowPopup={setShowPopup}
                                            />} />
              <Route path="cart" element={<CartPage/>}/>
            </Routes>
          <Footer/>
      </div>
    </Router>
  );
}

export default App;
