import React, { useState } from 'react'
import { Product } from '../../model/Product'
import ShopItem from './ShopItem'
import './ShopPage.css'

interface Props{
    listProduct:Product[]
}

export default function ProductsShow(props:Props) {
    const [listShow,setListShow] = useState<Product[]>(props.listProduct)
    const [inputSearch,setInputSearch]= useState('')

    const onCLickSearch=()=>{
        setListShow(listShow.filter(item=>item.name.includes(inputSearch)))
    }

    const onCLickRefresh=()=>{
        setListShow(props.listProduct)
    }


    const sortArray =(e:any)=>{
        if(e==="AZ"){
            setListShow(listShow.sort((a,b)=>(a.name.toLowerCase()>b.name.toLowerCase()) ? 1 : -1))
        }else if(e==="ZA"){
            setListShow(listShow.sort((a, b) => (a.name < b.name) ? 1 : -1))
        }else if(e==="Ascend"){
            setListShow(listShow.sort((a, b) => (a.price > b.price) ? 1 : -1))
        }else if(e==="Descend"){
            setListShow(listShow.sort((a, b) => (a.price < b.price) ? 1 : -1))
        }else{
            setListShow(props.listProduct)
        }
    }

    return (
        <div id="shopping-container">
            <div className="shop-show">
                <div className="title-bar">
                    <div className="group-search">
                        <input type="text" className="search-product" placeholder='Search' onChange={e=> setInputSearch(e.target.value)}/>
                        <button className="btn" onClick={onCLickSearch}>Search</button>
                        <button className="btn" onClick={onCLickRefresh}>Refresh</button>
                    </div>
                    <select id="dropdown" onChange={(e) => sortArray(e.target.value)}>
                        <option value="N/A" className="txtSort">Sort</option>
                        <option value="AZ">Name: A-Z</option>
                        <option value="ZA">Name: Z-A</option>
                        <option value="Ascend">Price: Ascending</option>
                        <option value="Descend">Price: Descending</option>
                    </select>
                </div>
                <div className="shop-list-container">
                    {listShow.map((item)=><ShopItem key={item.productId}  product={item}/>)}
                </div>
            </div>
        </div>
    )
}
