import React, { useState } from 'react'
import { getListFromLocal, ItemCart}  from '../../model/ItemCart'
// import from '../../model/ItemCart'

interface Props{
    itemCart:ItemCart
}

interface State{
    itemCarts:ItemCart[]
}

export default function ItemCartRender(props:Props) {

    const [state,setState]=useState<State>({itemCarts:getListFromLocal()})

    const onChangeQuantity=(e:any)=>{
        let index = state.itemCarts.find(item=>item.id===props.itemCart.id)
        setState({...state,itemCarts : state.itemCarts[Number(index)].quantity=e})
    }

    return (
        <tr className="table-row">
            <td className="table-column-1">
                <div className="item-cart-render">
                    <img src={props.itemCart.image} alt="" />
                    <div className="item-cart-info">
                        <p className="item-cart-name">Name: {props.itemCart.name}</p>
                        <small className="item-cart-price">Price:{props.itemCart.price}$</small>
                        <p className="item-cart-remove">Remove</p>
                    </div>
                </div>
            </td>
            <td className='table-column-2'>
                <button><i className="fas fa-minus"></i></button>

                <input type="number" name="" id="" defaultValue={+props.itemCart.quantity} onChange={e=>onChangeQuantity(e.target.value)}/>

                <button><i className="fas fa-plus"></i></button>
            </td>
            <td className='table-column-3'>  {+props.itemCart.quantity*props.itemCart.price}$</td>
        </tr>
    )
}
