import axios from "axios";
import { Product } from "../model/Product";


class ProductController{

    async list():Promise<Product[]>{
        return axios.get('http://localhost:3333/products').then(res=>{
            return res.data;
        });
    }

    async add(product:Product):Promise<Product[]>{
        return axios.put('http://localhost:3333/products/add',product) .then(res=>{
            return res.data;
        })
    }

    async update(product:Product):Promise<Product[]>{
        return axios.put('http://localhost:3333/products/update',product) .then(res=>{
            return res.data;
        })
    }

    async delete(productId:String):Promise<Product[]>{
        return axios.get(`http://localhost:3333/products/delete/${productId}`) .then(res=>{
            return res.data;
        })
    }

    async search(name:String){
        return axios.put(`http://localhost:3333/products/search/`,name).then(res=>{
            return res.data as Product[]
        })
    }

    async detail(id:String){
        return axios.get(`http://localhost:3333/product/detail/${id}`).then(res=>{
            return res.data as Product
        })
    }

    async pageShow(page:number){
        return axios.get(`http://localhost:3333/shop?page=${page}`) .then(res=>{
            let listProduct:Product[]=res.data.list
            let number:number[]=Array.from({length: res.data.total}, (_, i) => i + 1)
            return {listProduct,number}
        })
    }

}

export const productController = new ProductController();
