import { Image } from "./Image"

export interface Product{
    productId:string;
    price:number;
    name:string;
    desc?:string;
    // image:Image[];
    image?:string;
}

export const listIphone:Product[]=[
    {
        productId:"12",
        price:10,
        name:"iphone 13",
        desc:"lorem11111",
