import './Products.css'
import React from 'react'
import {ProductItem} from './ProductItem'
import { listIphone, Product } from '../../model/Product'
import { useState } from 'react'
import Form from './Form'
import { v4 as uuid } from 'uuid';

interface State {
    products: Product[]
}

export default function Products() {
    const [state, setState] = useState<State>({products:listIphone});

    const [productEdit,setProductEdit] = useState<Product>({productId:"",name:"",price:0,image:""})

    const onDelete = (idProduct:string) => {
        let listProduct=state.products;
        listProduct=listProduct.filter(item=>item.productId!=idProduct)
        setState({products:listProduct})
    };

    const addProduct=(product:Product) =>{
        let listProduct=state.products.slice();
        if(product.productId==""){
            product.productId=uuid();
            listProduct.push(product)
            console.log(product);
            let productClear={productId:"",name:"",price:0,image:"",desc:""};
            setProductEdit(productClear)
        }else{
            console.log(product);

            listProduct.map(item=> item.productId!==product.productId ? item:product)
        }
        setState({products:listProduct})
    }


    const onShowInfo =(product:Product)=>{
        setProductEdit({...product})
    }

    console.log(productEdit);

    return (
        <section id="product-container">
            <div className="title-button">
                <h1 className="product-container-title">Danh Sách sản phẩm</h1>
            </div>
            <Form key={uuid()} addProduct={addProduct} productEdit={productEdit} />
            <div className="list-product">
                {state.products.map((item)=>  <ProductItem product={item} onDelete={onDelete} onShowInfo={onShowInfo}  />)}
            </div>

        </section>
    )
}
