import './Products.css'
import React from 'react'
import {ProductItem} from './ProductItem'
import { listIphone, Product } from '../../model/Product'
import { useState } from 'react'
import Form from './Form'


interface State {
    products: Product[]
}

export default function Products() {
    const [state, setState] = useState<State>({products:listIphone});


    const onDelete = (idProduct:string) => {
        let listProduct=state.products;
        listProduct=listProduct.filter(item=>item.productId!=idProduct)
        setState({products:listProduct})
    };

    const addProduct=(product:Product) =>{
        let listProduct=state.products;
        product.productId=Math.random().toString(36).replace(/[^a-z]+/g, '').substr(0, 5);
        listProduct.push(product)
        setState({products:listProduct})
    }

    const editProduct=(product:Product)=>{
        console.log("edit");
    }

    const onShowInfo =(idProduct:string)=>{
        let listProduct=state.products;
        listProduct=listProduct.filter(item=>item.productId == idProduct)
        console.log(listProduct);

    }


    return (
        <section id="product-container">
            <div className="title-button">
                <h1 className="product-container-title">Danh Sách sản phẩm</h1>

            </div>
            <Form  addProduct={addProduct}/>
            <div className="list-product">
                {state.products.map((item)=>  <ProductItem product={item} onDelete={onDelete} onShowInfo={onShowInfo}/>)}
            </div>

        </section>
    )
}
