import React, { useState } from 'react'
import { Product } from '../../model/Product';


export interface Props {
    addProduct: (product:Product)=> void
    productEdit:Product
    editProduct:(product:Product)=>void
};

export default function Form(props:Props) {

    const [newProduct, setNewProduct] = useState<Product>({productId:"",name:"",price:0,image:""});

    const inputHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        setNewProduct({...newProduct, [e.target.name]: e.target.value})
    };

    console.log(newProduct);

    const editProduct =()=>{
        setNewProduct({...newProduct, [newProduct.productId]: props.productEdit.productId})
        props.editProduct(newProduct)
    }

    return (
        <div className="form-add">
            <div className="form-container">
                <div className="form-input">
                    <div className="input-name">
                        <label htmlFor="">Name:</label>
                        <input type="text" name="name" onChange={inputHandler} placeholder='Name' className="name-product" defaultValue={props.productEdit?.name}/>
                    </div>
                    <div className="input-name">
                        <label htmlFor="">Price:</label>
                        <input type="text" name="price" onChange={inputHandler} placeholder="Price" className="price-product" defaultValue={props.productEdit?.price.toString()}/>
                    </div>
                    <div className="input-name">
                        <label htmlFor="">Image url:</label>
                        <input type="text" name="image" onChange={inputHandler} placeholder="ImageUrl" className="img-product" defaultValue={props.productEdit?.image}/>
                    </div>
                    <div className="input-name">
                        <label htmlFor="">Desc:</label>
                        <input type="text" name="desc" onChange={inputHandler} placeholder="Desc" className="desc-product" defaultValue={props.productEdit?.desc}/>
                    </div>

                </div>
                <div className="group-button">
                    <button className="edit-product btn" onClick={()=>{props.addProduct(newProduct)}}>Add</button>
                    <button className="edit-product btn" onClick={editProduct}>Edit</button>
                    {/* <button className="edit-product btn" >Remove</button> */}
                </div>
            </div>
        </div>
    )
}
