import './Products.css'
import React from 'react'
import {ProductItem} from './ProductItem'
import { listIphone, Product } from '../../model/Product'
import { useState } from 'react'


interface State {
    products: Product[]
}

export default function Products() {
    const [state, setState] = useState<State>({products:listIphone});

    const [value,setValue] = useState({inputValue:""});


    const handleOnDelete = (idProduct:string) => {
        let products=state.products;
        products=products.filter(item=>item.productId!=idProduct)
        setState({products:products})
    };

    const inputHandler = (event: React.ChangeEvent<HTMLInputElement>) => {
        const value = event.target.value;
      };


    return (
        <section id="product-container">
            <div className="title-button">
                <h1 className="product-container-title">Danh Sách sản phẩm</h1>

            </div>
            <div className="form-add">
                <div className="form-container">
                    <div className="form-input">
                        <input type="text"  onChange={inputHandler} placeholder='Name' className="name-product" />
                        <input type="text" onChange={inputHandler} placeholder="Price" className="price-product" />
                        <input type="text" onChange={inputHandler} placeholder="ImageUrl" className="img-product" />
                        <input type="text" onChange={inputHandler} placeholder="Desc" className="desc-product" />
                    </div>
                    <div className="group-button">
                        <button className="edit-product btn">Add</button>
                        <button className="edit-product btn">Edit</button>
                        <button className="edit-product btn">Remove</button>
                    </div>
                </div>
            </div>
            <div className="list-product">
                {state.products.map((item)=>  <ProductItem product={item} handleOnDelete={()=>handleOnDelete} />)}
            </div>

        </section>
    )
}
