import './Products.css'
import React from 'react'
import ProductItem from './ProductItem'
import { listIphone, Product } from '../../model/Product'
import { useState } from 'react'

export default function Products() {
    const [listProduct, setListProduct] = useState<Product[]>(listIphone);

    const handleOnDelete = (idProduct:string) => {
        setListProduct((prev) => prev.filter((item) => item.productId !== idProduct));
    };

    return (
        <section id="product-container">
            <div className="title-button">
                <h1 className="product-container-title">Danh Sách sản phẩm</h1>
                <div className="group-button">
                    <button className="edit-product btn">Add</button>
                    <button className="edit-product btn">Edit</button>
                    <button className="edit-product btn">Remove</button>
                </div>
            </div>
            <div className="form-add">
                <input type="text" name="" id="" className="name-product" />
                <input type="text" name="" id="" className="price-product" />
                <input type="text" name="" id="" className="img-product" />
                <input type="text" name="" id="" className="desc-product" />
            </div>
            <div className="list-product">
                {listProduct.map((item)=>  <ProductItem product={item}  handleOnDelete={handleOnDelete}/>)}
            </div>

        </section>
    )
}
