import './App.css';
import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import HomePage from './page/home/HomePage';
import Header from './component/Header';
import Footer from './component/Footer';
import Products from './page/admin/ProductsAdmin';
import CartPage from './page/cart/CartPage';
import { Product } from './model/Product'
import { v4 as uuid } from 'uuid';
import ShopPage from './page/shop/ShopPage';
import ItemCart, { getListFromLocal } from './model/Cart'
import axios from 'axios'
import { productController } from './controller/ProductController';
import { createScanner } from 'typescript';


export interface State {
  products: Product[]
  product: Product
  isEdit:Boolean
  showPopup:Boolean
  countItemCart:number
  carts:ItemCart[]
}

function App() {

  const [state, setState] = useState<State>({
    products: [],
    product:  {id:"",name:"",price:0,image:""},
    isEdit:false,
    showPopup:false,
    countItemCart:getListFromLocal().length,
    carts :getListFromLocal()
  });

  console.log(state.carts.length);


  useEffect(() => {
    productController.list().then(res=>{
      setState({...state,products:res})
    })
  }, []);

  const onEditForm =(isEdit:Boolean)=>{
    setState({...state,isEdit:isEdit})
  }

  const onEditShowPopup = (showPopup:Boolean)=>{
    setState({...state,showPopup: showPopup})
  }

  const onDelete = (idProduct:string) => {
    productController.delete(idProduct).then(res=>{
      setState({...state,products:res})
    })
  };

  const onAddEditProduct=(product:Product) =>{
    let listProduct:Product[]=state.products;
    if(product.id===""){
        product.id=uuid();
        productController.add(product).then(res=>{
          setState({...state,products:res})
        })
    }else{
        productController.update(product).then(res=>{
          setState({...state,products:res})
        })
    }

    setState({...state,products:listProduct})
    let productClear={id:"",name:"",price:0,image:""};
    setState({...state,product:productClear})
  }

  const onClickShowInfo =(productShow:Product)=>{
    console.log(productShow);
    setState({...state,product:productShow,isEdit:true,showPopup:true})
    // setIsEdit(true)
    // setShowPopup(true)
  }

  const setListProductToLocal=(listProduct:Product[])=>{
    localStorage.setItem('listProduct',JSON.stringify(listProduct))
  }

  //cart
  const onClickAddToCart = (id:String) => {
    const addedItem = state.products.find(item => item.id === id);
    // const addedItemExists = state.carts.some(item => item.id === id);
    const findIndex=state.carts.find(item => item.id === id);
    console.log(id);

    if (findIndex) {
      console.log();

      // setCart((prev) => prev.map((item) => (
      //   item.id === id
      //     ? { ...item, quantity: item.quantity + 1 }
      //     : item
      // )));

      // setState({...state,carts:{...state.carts}})

      // setState({...state,newProduct:{...state.newProduct,price:Number(e.target.value)} })

    } else {
      const newItemCart:ItemCart={id:id,quantity:1}
      const list:ItemCart[] = state.carts
      list.push(newItemCart)
      setState({...state,carts:list})
      // setCart((prev) => ([ ...prev, { ...addedItem, quantity: 1 } ]));
    }
  }


  return (
    <Router>
      <div>
          <Header countItemCart={state.countItemCart} />
            <Routes>
              <Route path="/" element={<HomePage/>}/>
              <Route path="home" element={<HomePage/>}/>
              <Route path="shop" element={<ShopPage key={uuid()}   
                                            listProduct={state.products} 
                                            onClickAddToCart={onClickAddToCart}
                                          />}/>
              <Route path="admin" element={<Products  key={uuid()}
                                                      onAddEditProduct={onAddEditProduct}
                                                      productEdit={state.product} 
                                                      onDelete={onDelete} 
                                                      onClickShowInfo={onClickShowInfo}
                                                      products={state.products}
                                                      isEdit={state.isEdit}
                                                      onEditForm={onEditForm}
                                                      showPopup={state.showPopup}
                                                      onEditShowPopup={onEditShowPopup}
                                            />} />
              <Route path="cart" element={<CartPage />}/>
            </Routes>
          <Footer/>
      </div>
    </Router>
  );
}

export default App;
