import React, { useState } from 'react'
import { createScanner } from 'typescript'
import ItemCart from '../../model/Cart'
import './CartPage.css'
import ItemCartRender from './ItemCartRender'

interface Props{
    carts:ItemCart[]
}

export default function CartPage(props:Props) {

    const showCart=()=>{
        if(props.carts.length>0){
            return  <div>
                        <table className="show-list">
                            <tr className="table-title">
                                <th><p>Product</p></th>
                                <th><p>Quantity</p></th>
                                <th><p>Subtotal</p></th>
                            </tr>

                            {props.carts.map(item=><ItemCartRender key={+item.id} itemCart={item}/>)}

                            <tr className="table-row">
                                <td></td>
                                <td className='table-column-2'>Total</td>
                                <td className='table-column-3'>600$</td>
                            </tr>
                        </table>
                        <button className='btn'>Order</button>
                    </div>      
        }else{
            return <div className='cart-empty'>
                <h1>Your cart is Empty</h1>
            </div>
        }
    }

    return (
        <div id="cart-page-container">
            <div className="cart-page">
                <h1 className="cart-page-title">Your Cart:</h1>
                <div className="show-list-container">
                    {showCart()}
                </div>
            </div>
        </div>
    )
}
