import React, { useState } from 'react'
import { Product } from '../../model/Product';


export interface Props {
    onAddEditProduct: (product:Product)=> void
    productEdit:Product
};

export default function Form(props:Props) {

    const [newProduct, setNewProduct] = useState<Product>(props.productEdit);
    const [errName,setErrName] =useState('')
    const [errPrice,setErrPrice] =useState('')
    const [errImage,setErrImage] =useState('')

    const handleSubmit =(e:any)=>{
        e.preventDefault();
        const isValid=formValidation()

        props.onAddEditProduct(newProduct)
    }

    const formValidation =()=>{
        let nameErr = '';
        let priceErr = '';
        let imageErr = '';
        let isValid=true

        if(newProduct.name.length==0){
            nameErr='Ten khong duoc de trong';
            return false
        }

        if(newProduct.price==0){
            priceErr='Gia phai lon hon 0';
            return false
        }

        if(newProduct.image?.length==0){
            imageErr='Hinh anh khong duoc de trong';
            return false
        }

        setErrName(nameErr)
        setErrPrice(priceErr)
        setErrImage(imageErr)

        return isValid
    }


    // const errorNameNull = newProduct.name.length==0 ? 'This field is require' : ''
    // const errorNameLength= newProduct.name.length>30 ? 'Max length is 30':""




    return (
        <div className="form-add">
            <div className="form-container">
                <form className="form-input" onSubmit={handleSubmit}>
                    <div className="input-name">
                        <label htmlFor="">Name:</label>
                        <input type="text" name="name" onChange={e=>{setNewProduct({...newProduct, name: e.target.value})}} className="name-product" value={newProduct.name} required/>
                        {/* {errorNameNull.length>0 && <label  className="show-err err-name">{errorNameNull}</label>}
                        {errorNameNull.length<30 && <label  className="show-err err-name">{errorNameLength}</label>} */}
                    </div>
                    <div className="input-name">
                        <label htmlFor="">Price:</label>
                        <input type="text" name="name" onChange={e=>{setNewProduct({...newProduct, price: Number(e.target.value)})}} className="price-product" value={newProduct.price} required/>
                        <label  className="show-err err-price"> </label>
                    </div>
                    <div className="input-name">
                        <label htmlFor="">Image url:</label>
                        <input type="text" name="image" onChange={e=>{setNewProduct({...newProduct, image: e.target.value})}}  className="img-product" value={newProduct.image} required/>
                        <label  className="show-err err-image"> &nbsp;</label>
                    </div>
                    <div className="input-name">
                        <label htmlFor="">Desc:</label>
                        <input type="text" name="desc" onChange={e=>{setNewProduct({...newProduct, desc: e.target.value})}}  className="desc-product" value={newProduct.desc}/>
                        <label  className="show-err err-desc"> &nbsp;</label>
                    </div>

                    <div className="group-button">
                    <button className="edit-product btn" type='submit'>Add</button>
                    <button className="edit-product btn" type='submit'>Edit</button>
                    {/* <button className="edit-product btn" >Remove</button> */}
                </div>
                </form>

            </div>
        </div>
    )
}
