import React, { useState } from 'react'
import { Product } from '../../model/Product'
import ShopItem from './ShopItem'
import './ShopPage.css'

interface Props{
    listProduct:Product[]
    onClickAddToCart:(id:String)=>void
}

interface State{
    listShow:Product[],
    inputSearch:string
}

export default function ProductsShow(props:Props) {
    // const [listShow,setListShow] = useState<Product[]>(props.listProduct)
    // const [inputSearch,setInputSearch]= useState('')

    const [state,setState]=useState<State>({
        listShow:props.listProduct,
        inputSearch:""
    })

    const onCLickSearch=()=>{
        // setListShow(listShow.filter(item=>item.name.includes(inputSearch)))
        // const listNew = listShow.filter(item=>item.name.includes(inputSearch))

        setState({...state,listShow:state.listShow.filter(item=>item.name.includes(state.inputSearch))})
    }

    const onCLickRefresh=()=>{
        setState({...state,listShow:props.listProduct})
    }


    const sortArray =(e:any)=>{
        const listSort= state.listShow.slice()
        if(e==="AZ"){
            // setListShow(listSort.sort((a, b) => (a.name.toLowerCase()>b.name.toLowerCase()) ? 1 : -1))
            setState({...state,
                listShow: listSort.sort((a, b) => (a.name.toLowerCase()>b.name.toLowerCase()) ? 1 : -1)
            })
        }else if(e==="ZA"){
            setState({...state,
                listShow:listSort.sort((a, b) => (a.name.toLowerCase()<b.name.toLowerCase()) ? 1 : -1)
        })
        }else if(e==="Ascend"){
            // setListShow(listSort.sort((a, b) => (a.price > b.price) ? 1 : -1))
            setState({...state,
                listShow:listSort.sort((a, b) => (a.price > b.price) ? 1 : -1)
            })
        }else if(e==="Descend"){
            // setListShow(listSort.sort((a, b) => (a.price < b.price) ? 1 : -1))
            setState({...state,
                listShow:listSort.sort((a, b) => (a.price < b.price) ? 1 : -1)
            })
        }else{
            // setListShow(listShow)
            setState({...state,
                listShow:props.listProduct
            })
        }
    }

    return (
        <div id="shopping-container">
            <div className="shop-show">
                <div className="title-bar">
                    <div className="group-search">
                        <input type="text" className="search-product" placeholder='Search' onChange={e=> setInputSearch(e.target.value)}/>
                        <button className="btn" onClick={onCLickSearch}>Search</button>
                        <button className="btn" onClick={onCLickRefresh}>Refresh</button>
                    </div>
                    <select id="dropdown" onChange={(e) => sortArray(e.target.value)}>
                        <option value="N/A" className="txtSort">Sort</option>
                        <option value="AZ">Name: A-Z</option>
                        <option value="ZA">Name: Z-A</option>
                        <option value="Ascend">Price: Ascending</option>
                        <option value="Descend">Price: Descending</option>
                    </select>
                </div>
                <div className="shop-list-container">
                    {state.listShow.map((item)=><ShopItem key={item.id}  product={item} onClickAddToCart={props.onClickAddToCart}/>)}
                </div>
            </div>
        </div>
    )
}
