import { Image } from "./Image"

export interface Product{
    productId:string;
    price:number;
    name:string;
    desc?:string;
    // image:Image[];
    image?:string;
}

export const listIphone:Product[]=[
    {
        productId:"12",
        price:10,
        name:"iphone 13",
        desc:"lorem11111",
        image:"https://cdn.tgdd.vn/Products/Images/42/213033/iphone-12-pro-max-xanh-duong-new-600x600-600x600.jpg"
